    "apiKeyValue": {% if data.secrets.shopify_access_token is defined %}"{{data.secrets.shopify_access_token}}"{% else %}""{% endif %},
    "storeName": {% if data.secrets.shopify_store_name is defined %}"{{data.secrets.shopify_store_name}}"{% else %}""{% endif %}
  },
  "params": {
    "limit": "10"
  }
}

function handler(params) {
  const shopifyProducts = params.data.steps.getShopifyProducts.output.products || [];

  const transformedProducts = shopifyProducts.map(product => {
    // Transform Shopify product to BigCommerce format
    const bcProduct = {
      name: product.title || 'Unknown Product',
      type: 'physical',
      price: parseFloat(product.variants?.[0]?.price) || 0,
      weight: product.variants?.[0]?.grams ? Math.round(product.variants[0].grams / 28.35) : 1, // Convert grams to ounces
      description: product.body_html || product.title || '',
      sku: product.variants?.[0]?.sku || '',
      is_visible: product.status === 'active',
      inventory_level: product.variants?.[0]?.inventory_quantity || 0,
      inventory_tracking: 'product'
    };

    return bcProduct;
  });

  return {
    transformedProducts: transformedProducts,
    productCount: transformedProducts.length,
    originalProducts: shopifyProducts
  };
}

