{
  "auth": {
    "apiKeyValue": {% if data.secrets.shopify_access_token is defined %}"{{data.secrets.shopify_access_token}}"{% else %}""{% endif %},
    "storeName": {% if data.secrets.shopify_store_name is defined %}"{{data.secrets.shopify_store_name}}"{% else %}""{% endif %}
  },
  "params": {
    "limit": "10"
  }
}

function handler(params) {
  const product = params.data.steps.processProductsLoop.loopOverItem;

  // Transform Shopify product to BigCommerce format
  const transformedProduct = {
    name: product.title || 'Unknown Product',
    description: product.body_html || product.title || 'Product description',
    type: 'physical',
    weight: product.variants && product.variants.length > 0 ? product.variants[0].weight || 1 : 1,
    price: product.variants && product.variants.length > 0 ? parseFloat(product.variants[0].price) || 0 : 0,
    is_visible: true,
    inventory_tracking: 'none'
  };

  // Add optional fields if available
  if (product.vendor) {
    transformedProduct.search_keywords = product.vendor;
  }

  if (product.variants && product.variants.length > 0 && product.variants[0].sku) {
    transformedProduct.sku = product.variants[0].sku;
  }

  return {
    productData: transformedProduct,
    originalTitle: product.title,
    shopifyId: product.id
  };
}

