function handler(params) {
  const productsCount = params.data.steps.getProducts.output.products ? params.data.steps.getProducts.output.products.length : 0;

  const message = `🎉 Product sync completed successfully!\n\n` +
    `✅ Total products processed: ${productsCount}\n` +
    `📦 Products transferred from Shopify to BigCommerce\n` +
    `⏰ Sync completed at: ${new Date().toLocaleString()}\n\n` +
    `Data sync operation completed in the #ai_testing channel.`;

  return {
    message: message,
    count: productsCount,
    status: 'completed'
  };
}

