function handler(params) {
  const attributeData = [];
  const nullDateCustomerIds = [];
  const batchSize = params['data']['var']['customerAttributeValueBatchSize'];
  const attributeId = params['data']['var']['attributeID'];
  const customerData = params['data']['var']['customerBCData'];
  const key = params['data']['var']['giField'];
  const type = params['data']['var']['type'];

  for(let i = 0; i < batchSize; i++) {
    let rawValue = customerData[i]['data'][key];
    let value;
    if (type === "date") {
      if (rawValue === null) {
        nullDateCustomerIds.push(customerData[i]['bCCustomerID']);
        continue;
      } else {
        value = rawValue.match(/^(.*?)T/)[1];
      }
    } else {
      value = rawValue === null ? "" : rawValue;
    }

    const attribute = {
        "attribute_id": attributeId,
        "customer_id": customerData[i]['bCCustomerID'],
        "value": value
    };
    attributeData.push(attribute);
  }

  return {attributeData, nullDateCustomerIds};
}

