function handler(params) {
  const emailRecords = params['data']['var']['recordsFromDB'];

  const batchSize = params['data']['var']['customerEmailBatchSize'];

  const emailBatches = [];
  for (let i = 0; i < emailRecords.length; i += batchSize) {
    const batch = emailRecords
      .slice(i, i + batchSize)
      .map(item => item.record_key)
      .join(',');
    emailBatches.push(batch);
  }

  return emailBatches;
}

