function handler(params) {
  const attributeData = [];
  const batchSize = params['data']['var']['customerAttributeValueBatchSize'];
  const attributeId = params['data']['var']['attributeID'];
  const customerData = params['data']['var']['customerBCData'];
  const key = params['data']['var']['giField'];
  const type = params['data']['var']['type'];

  for(let i = 0; i < batchSize; i++) {
    let value;
    if (type === "date") {
      value = customerData[i]['data'][key] === null ? "1970-01-01" : customerData[i]['data'][key].match(/^(.*?)T/)[1];
    } else {
      value = customerData[i]['data'][key] === null ? "" : customerData[i]['data'][key];
    }

    const attribute = {
        "attribute_id": attributeId,
        "customer_id": customerData[i]['bc_id'],
        "value": value
    };
    attributeData.push(attribute);
  }

  return attributeData;
}

